import "./singlePost.css";
import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";


function SinglePost() {
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [post,  setPost] = useState({})

  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get("/posts/" + path);
      setPost(res.data)
    };
    getPost();
  }, [path]);
  return (
    <div className="singlePost">
      <div className="singlePostWrapper">
        <img src="" alt="" className="singlePostImg" />
        <h1 className="singlePostTitle">
          Lorem ipsum dolor, sit amet!
        <div className="singlePostEdit">
          <i className="postIcon fa fa-edit"></i>
          <i class="postIcon fa fa-trash"></i>
        </div>
        </h1>
        <div className="singlePostInfo">
          <span className="singlePostAuthor">
            Author: <b>Nastya</b>
          </span>
          <span className="singlePostDate">1 hour ago</span>
        </div>
        <p className="singlePostDesc">
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Quaerat
          officia vero doloribus deserunt quidem maxime illo tempore facilis hic
          et rem non, blanditiis nam reiciendis at quis! Esse, tenetur.
          Voluptate.
        </p>
      </div>
    </div>
  );
}

export default SinglePost;
